using System;
using System.Drawing;
using System.IO;
using System.Threading;

namespace WindowsCredentialProviderTest.QRCode
{
    public sealed class QRCodeBitmap
    {
        private readonly string qrCodeBase64;
        public QRCodeBitmap()
        {
            qrCodeBase64 = "";
        }
        public Bitmap GetBitmap()
        {

            //Decode the base64 string to bytes
            byte[] qrCodeBytes = Convert.FromBase64String(qrCodeBase64);

            //Create a MemoryStream from the decoded bytes
            using (MemoryStream ms = new MemoryStream(qrCodeBytes))
            {
                Image image = Image.FromStream(ms);
                //Create a Bitmap from the MemoryStream
                Bitmap bitmap = new Bitmap(image);

                //Save the Bitmap to a file
                //string outputPath = "output.bmp";
                //bitmap.Save(outputPath);
                return bitmap;
            }



        }
        public string GetQRCode()
        {
            return qrCodeBase64;
        }
    }
}

